<template>
<main>
  <div id="wrapperHead">

  <div id="header">
    <h1> Welcome to Pollify</h1>
    <h3> - Easy to create and participate</h3>
  </div>

  <div id="langDiv">
    <input class="langImg" type="image" src="data:image/png;base64," v-on:click="switchToSwedishLanguage"/>

    <input class="langImg" type="image" src="data:image/png;base64," v-on:click="switchToEnglishLanguage"/>
<!--    <input class="langImg" type="image" src="{{uiLabels.img}}" v-on:click="switchLanguage"/>-->
  </div>

    </div>

 <section id="wrapperButtons">

   <div id="divCreate">
    <button id="buttonCreate">
    <router-link  v-bind:to="'/create/'+lang">{{uiLabels.createPoll}}</router-link>
    </button>
   </div>
<!--    <label>
    Write poll id:
      <input type="text" v-model="id">
    </label> -->
    <div id="divPart">
    <button id="buttonParticipate">
    <router-link v-bind:to="'/poll/'+id" tag="button">{{uiLabels.participatePoll}}</router-link>
    </button>
    </div>
 </section>

</main>


</template>

<script>
import io from 'socket.io-client';
const socket = io();

export default {
  name: 'Start',
  data: function () {
    return {
      uiLabels: {},
      id: "",
      lang: "en"
    }
  },
  created: function () {
    socket.on("init", (labels) => {
      this.uiLabels = labels
    })
  },
  methods: {
    switchToSwedishLanguage: function() {
      if (this.lang === "en")
        this.lang = "sv"
      socket.emit("switchLanguage", this.lang)
    },
    switchToEnglishLanguage: function() {
      if (this.lang === "sv")
        this.lang = "en"
      socket.emit("switchLanguage", this.lang)
    }
  }
}
</script>

